bool exit(){
    system("CLS");
    std::cout << "Exited";
    return 0;
}
bool menuInput() {
    int counter = 1;
    int night = 1;

    

    int height = 15, width = 15;

    menu(counter);

    while (true)
    {
        switch (_getch()) {
        case KEY_UP:
        {
            if (counter == 1) counter = 5;
            counter--;
            system("CLS");
            menu(counter);
        } break;
        case KEY_DOWN:
        {
            if (counter == 4) {
                counter = 0;
            }

            counter++;
            system("CLS");
            menu(counter);
        } break;
        case ENTER: {
            switch (counter) {
            case 1: {

                while (night <= 5) {
                    runGame(&player, &enemy, &trace, &wall, night++);
                }
                system("CLS");
                menuInput();
                break;
            }
            case 2: {
                system("CLS");
                displaySettingsMenu(&player, &trace, &enemy, &wall);
                menuInput();
                break;
            }
            case 3: {
                system("CLS");
                displayHowToPlay();
                break;
            }
            case 4: {
                exit();
            }
            }
        } break;
        }
    }
}

